import { useEffect, useState } from "react";
import "./ClockIn.css";
import timeService from "../../../Services/TimeService";
import notifyService from "../../../Services/NotifyService";
import { Button } from "react-bootstrap";
import AttendanceModel from "../../../Models/AttendanceModel";
import Action from "../../../Models/ActionModel";

export function ClockIn(): JSX.Element {

    const [isIn, setIsIn] = useState<boolean>();
    const [timeInGermany, setTimeInGermany] = useState<string>();

    useEffect(() => {
        const setTime = () => {
            timeService.getTimeInGermany()
                .then(time => {
                    setTimeInGermany(new Date(time).toLocaleTimeString())
                })
                .catch(err => notifyService.error(err.message))
        }
        setTime();
        const interval = setInterval(setTime, 1000)
        return () => clearInterval(interval);
    }, [])

    const handleClockClick = () => {
        isIn === false ? setIsIn(true) : setIsIn(false);
        const attendance = new AttendanceModel({
            date : "",
            action : Action.In,


        })
    };

    return (
        <div className="ClockIn">
            <div className="d-grid gap-3">
                <div className="display">{timeInGermany}</div>
                <Button onClick={handleClockClick} variant={isIn ? "danger" : "success"} size="lg">
                    {isIn ? <>Clock Out</> : <>Clock In</>}
                </Button>
            </div>
        </div>
    );
}
