import { Button } from "react-bootstrap";
import Action from "../../../Models/ActionModel";
import AttendanceModel from "../../../Models/AttendanceModel";
import notifyService from "../../../Services/NotifyService";
import timeService from "../../../Services/TimeService";
import "./AttendanceButton.css";

type AttendanceButtonProps = {
    isIn: boolean;
    timeInGermany: string;
};

export function AttendanceButton(props: AttendanceButtonProps): JSX.Element {

    const handleClockClick = async (): Promise<void> => {
        // props.isIn === false ? setIsIn(true) : setIsIn(false);
        const attendance = new AttendanceModel({
            date: props.timeInGermany.substring(0, 10),
            action: props.isIn === false ? Action.In : Action.Out,
            time: props.timeInGermany.substring(11)
        });
        console.log(attendance);
        timeService.attendance(attendance)
            .then(() => notifyService.success(props.isIn === false ? "Bye ByeðŸ‘‹" : "Have a productive timeðŸ¤©"))
            .catch(err => notifyService.error(err.message))
    };


    return (
        <div className="AttendanceButton">
            <Button onClick={handleClockClick} variant={props.isIn ? "danger" : "success"} size="lg">
                {props.isIn ? <>Clock Out</> : <>Clock In</>}
            </Button>
        </div>
    );
}
