import { useEffect, useState } from "react";
import "./ClockIn.css";
import timeService from "../../../Services/TimeService";
import notifyService from "../../../Services/NotifyService";
import { Button } from "react-bootstrap";

export function ClockIn(): JSX.Element {

    const [timeInGermany, setTimeInGermany] = useState<Date>()
    useEffect(() => {
        console.log("been here")
        const timeOut = markTime();
        return () => clearTimeout(timeOut);
    }, [])

    function markTime(): NodeJS.Timeout {
        timeService.getTimeInGermany()
            .then(time => {
                setTimeInGermany(new Date(time))
            })
            .catch(err => notifyService.error(err.message))
        const timeOut = setTimeout(markTime, 1000);
        return timeOut;
    }

    return (
        <div className="ClockIn">
            <div className="d-grid gap-3">
                <div className="display">
                    <div className="digits hour">{timeInGermany && timeInGermany.getHours()}</div>
                    <span>:</span>
                    <div className="digits minute">{timeInGermany && timeInGermany.getMinutes()}</div>
                    <span>:</span>
                    <div className="digits second">{timeInGermany && timeInGermany.getSeconds()}</div>
                </div>
                <br />
                <Button variant="success" size="lg">
                    Have a Prodoctive  Day!
                </Button>
            </div>
        </div>
    );
}
