import React, { useState } from 'react';
import './AttendanceTable.css'; // Import CSS file

interface AttendanceTableProps {}

const AttendanceTable: React.FC<AttendanceTableProps> = () => {
  const currentDate = new Date();
  const [year, setYear] = useState<number>(currentDate.getFullYear());
  const [month, setMonth] = useState<number>(currentDate.getMonth());
  const [selectedEmployee, setSelectedEmployee] = useState<string>('');

  const daysInMonth = (year: number, month: number): number => {
    return new Date(year, month + 1, 0).getDate();
  };

  const handleMonthChange = (event: React.ChangeEvent<HTMLSelectElement>) => {
    setMonth(parseInt(event.target.value));
  };

  const handleYearChange = (event: React.ChangeEvent<HTMLSelectElement>) => {
    setYear(parseInt(event.target.value));
  };

  const handleEmployeeChange = (event: React.ChangeEvent<HTMLSelectElement>) => {
    setSelectedEmployee(event.target.value);
    // Fetch and display hours for the selected employee here
  };

  const days = daysInMonth(year, month);

  return (
    <div className="AttendanceTable-container">
      <div className="AttendanceTable-header">
        <label htmlFor="month">Month:</label>
        <select id="month" value={month} onChange={handleMonthChange}>
          {Array.from({ length: 12 }, (_, i) => (
            <option key={i} value={i}>
              {new Date(2000, i, 1).toLocaleString('en-US', { month: 'long' })}
            </option>
          ))}
        </select>
        &nbsp;
        <label htmlFor="year">Year:</label>
        <select id="year" value={year} onChange={handleYearChange}>
          {Array.from({ length: 10 }, (_, i) => (
            <option key={i} value={year - 5 + i}>
              {year - 5 + i}
            </option>
          ))}
        </select>
        <label htmlFor="employee">Employee:</label>
        <select id="employee" value={selectedEmployee} onChange={handleEmployeeChange}>
          <option value="">Select an employee</option>
          <option value="employee1">Employee 1</option>
          <option value="employee2">Employee 2</option>
          {/* Add more options for other employees */}
        </select>
      </div>
      <table className="AttendanceTable-table">
        <thead>
          <tr>
            <th>Date</th>
            <th>Got In</th>
            <th>Got Out</th>
            <th></th> {/* Empty column */}
          </tr>
        </thead>
        <tbody>
          {Array.from({ length: days }, (_, i) => (
            <tr key={i}>
              <td>{`${i + 1}/${month + 1}`}</td>
              <td>{/* Employee's got in time */}</td>
              <td>{/* Employee's got out time */}</td>
              <td></td> {/* Empty column */}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default AttendanceTable;
