import { useEffect, useState } from "react";
import "./ClockIn.css";
import timeService from "../../../Services/TimeService";
import notifyService from "../../../Services/NotifyService";
import { AttendanceButton } from "../AttendanceButton/AttendanceButton";

export function ClockIn(): JSX.Element {

    const [timeInGermany, setTimeInGermany] = useState<string>();
    const isIn = false;
    useEffect(() => {
        const setTime = () => {
            timeService.getTimeInGermany()
                .then(time => {
                    setTimeInGermany(time)
                })
                .catch(err => notifyService.error(err.message))
        }
        setTime();
        const interval = setInterval(setTime, 1000)
        return () => clearInterval(interval);
    }, [])



    return (
        <div className="ClockIn">
            <div className="d-grid gap-3">
                <div className="display">{new Date(timeInGermany).toLocaleTimeString()}</div>
                <AttendanceButton isIn={isIn === false ? true : false} timeInGermany={timeInGermany} />
            </div>
        </div>
    );
}
