import "./EditEmployeeAttendance.css";
import { useEffect, useState } from 'react';
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';
import timeService from "../../../Services/TimeService";
import AttendanceModel from "../../../Models/AttendanceModel";

type EditEmployeeAttendanceProps = {
    idArr: number[];
};

export function EditEmployeeAttendance(props: EditEmployeeAttendanceProps): JSX.Element {

    const [show, setShow] = useState(false);
    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);
    const [attendance, setAttendance] = useState<AttendanceModel[]>()

    useEffect(() => {
        const temp: AttendanceModel[] = [];
        props.idArr.forEach(id => timeService.getOneAttendance(id)
            .then(record => { temp.push(record); setAttendance(temp) })
            .catch(err => console.error(err))
        )
    }, [])

    return (
        <>
            <Button variant="primary" onClick={handleShow}>
                Edit
            </Button>

            <Modal show={show} onHide={handleClose}>
                <Modal.Header closeButton>
                    <Modal.Title>{attendance.map(record => record.date)}</Modal.Title>
                </Modal.Header>
                <Modal.Body>  <Container>
                    <Row>
                        <Col xs={9} md={6}>
                            Clock in- 8:30
                        </Col>
                        <Col xs={9} md={6}>
                            Clock out- 17:20
                        </Col>
                    </Row>
                </Container></Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={handleClose}>
                        Close
                    </Button>
                    <Button variant="primary" onClick={handleClose}>
                        Save Changes
                    </Button>
                </Modal.Footer>
            </Modal>
        </>
    );
}
