import { useEffect, useState } from "react";
import "./Header.css";
import Container from 'react-bootstrap/Container';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import NavDropdown from 'react-bootstrap/NavDropdown';
import Offcanvas from 'react-bootstrap/Offcanvas';
import store from "../../../Redux/Store";
import { authReducer } from "../../../Redux/AuthState";
import UserModel from "../../../Models/UserModel";
import { NavLink } from "react-router-dom";

export function Header(): JSX.Element {

    const [user, setUser] = useState<UserModel>()
    useEffect(() => {
        setUser(store.getState().authState.user)
        const unsubscribe = store.subscribe(() => {
            setUser(store.getState().authState.user)
            console.log("HereHereHere")
        })
        return unsubscribe;
    }, [])


    return (
        <div className="Header">
            <Navbar collapseOnSelect expand="lg" className="bg-body-tertiary bg-dark">
                <Container>
                    <NavLink to="/"><Navbar.Brand>Time Attendance</Navbar.Brand></NavLink>
                    <Navbar.Toggle aria-controls="responsive-navbar-nav" />
                    <Navbar.Collapse id="responsive-navbar-nav">
                        <Nav className="me-auto">
                            {user &&
                                <>
                                    <NavLink to="/clock"><Nav>Clock‚è∞</Nav></NavLink> &nbsp;
                                    <NavLink to="/my-attendance"><Nav>My AttendanceüìÖ</Nav></NavLink>
                                </>
                            }
                        </Nav>
                        <Nav>
                            {user ? `Hello ${user.username}` : `login`}
                        </Nav>
                    </Navbar.Collapse>
                </Container>
            </Navbar>
        </div>
    );
}
