import "./EditEmployeeAttendance.css";
import React, { useState } from 'react';
import Button from 'react-bootstrap/Button';
import Col from 'react-bootstrap/Col';
import Container from 'react-bootstrap/Container';
import Modal from 'react-bootstrap/Modal';
import Row from 'react-bootstrap/Row';
import AttendanceModel from "../../../Models/AttendanceModel";
import Action from "../../../Models/ActionModel";

type EditEmployeeAttendanceProps = {
    attendance: AttendanceModel;
};

export function EditEmployeeAttendance(props: any, attendanceProps: EditEmployeeAttendanceProps): JSX.Element {
    return (
        <Modal {...props} aria-labelledby="contained-modal-title-vcenter">
            <Modal.Header closeButton>
                <Modal.Title id="contained-modal-title-vcenter">
                    {attendanceProps.attendance.date}
                </Modal.Title>
            </Modal.Header>
            <Modal.Body className="grid-example">
                <Container>
                    <Row>
                        <Col xs={9} md={6}>
                            {attendanceProps.attendance.action === Action.In && attendanceProps.attendance.time}
                        </Col>
                        <Col xs={9} md={6}>
                        {attendanceProps.attendance.action === Action.Out && attendanceProps.attendance.time}
                        </Col>
                    </Row>
                </Container>
            </Modal.Body>
            <Modal.Footer>
                <Button onClick={props.onHide}>Close</Button>
            </Modal.Footer>
        </Modal>
    );
}
