import "./EditEmployeeAttendance.css";
import { useEffect, useState } from 'react';
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';
import timeService from "../../../Services/TimeService";
import AttendanceModel from "../../../Models/AttendanceModel";
import { Col, Container, Row } from "react-bootstrap";
import { useNavigate, useParams } from "react-router-dom";

export function EditEmployeeAttendance(): JSX.Element {

    const [show, setShow] = useState(true);
    const navigate = useNavigate();
    const handleClose = () => { setShow(false); navigate("/employees-attendance") };
    const [attendance, setAttendance] = useState<AttendanceModel[]>()
    let ids = useParams(); // Retrieve attendance IDs from route parameters

    useEffect(() => {
        const temp: AttendanceModel[] = [];
        Object.values(ids)?.forEach(id => {
            timeService.getOneAttendance(+id)
                .then(attendance => { temp.push(attendance); setAttendance(temp) })
                .catch(err => err.message)
        });
    }, [])

    return (
        <>
            <Modal show={show} onHide={handleClose}>
                <Modal.Header closeButton>
                    <Modal.Title>{attendance && attendance[0].date}</Modal.Title>
                </Modal.Header>
                <Modal.Body>  <Container>
                    <Row>

                        <Col xs={9} md={6}>
                            {/* {attendance && attendance[0]} */}
                        </Col>
                        <Col xs={9} md={6}>
                            {/* {attendance && attendance[1]} */}
                        </Col>
                    </Row>
                </Container></Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={handleClose}>
                        Close
                    </Button>
                    <Button variant="primary" onClick={handleClose}>
                        Save Changes
                    </Button>
                </Modal.Footer>
            </Modal>
        </>
    );
}
