import { useEffect, useState } from "react";
import "./ClockIn.css";
import timeService from "../../../Services/TimeService";
import notifyService from "../../../Services/NotifyService";
import { Button } from "react-bootstrap";

export function ClockIn(): JSX.Element {

    const [timeInGermany, setTimeInGermany] = useState<string>()
    useEffect(() => {
        const interval = setInterval(() => {
            timeService.getTimeInGermany()
                .then(time => {
                    setTimeInGermany(new Date(time).toLocaleTimeString())
                })
                .catch(err => notifyService.error(err.message))
        }, 1000)
        return () => clearInterval(interval);
    }, [])

    return (
        <div className="ClockIn">
            <div className="d-grid gap-3">
                <div className="display">{timeInGermany}</div>
                <Button variant="success" size="lg">

                </Button>
            </div>
        </div>
    );
}
