import axios from "axios";
import config from "../Utils/Config";
import AttendanceModel from "../Models/attendance-model";

class TimeService {

    public async getTimeInGermany(): Promise<string> {
        const response = await axios.get<string>(config.timeInGermanyUrl);
        const timeInGermany = response.data;
        return timeInGermany;
    }

    public async getUserAttendance(month: string): Promise<AttendanceModel[]> {
        const response = axios.get<AttendanceModel[]>(config.userAttendanceUrl + month);
        const userAttendance = (await response).data;
        return userAttendance;
    }

    public async clockin():Promise<AttendanceModel>

    public daysInMonth(month: number): number {
        return new Date(2024, month + 1, 0).getDate();
    };

}
const timeService = new TimeService();
export default timeService;
