const fs = require("fs");
const args = require("../logic/args");

function createModule() {
    const componentName = args.getComponentName();
    const moduleCompletePath = args.getModuleCompletePath();
    const foldersCompletePath = args.getComponentFolderCompletePath();
    const moduleContent = getModuleContent(componentName);
    if (!fs.existsSync(foldersCompletePath)) fs.mkdirSync(foldersCompletePath, { recursive: true });
    fs.writeFileSync(moduleCompletePath, moduleContent);
}

function getModuleContent(componentName) {
    return `.${componentName} {
\t
}
`;
}

module.exports = createModule;