const fs = require("fs");
const args = require("../logic/args");

function createScss() {
    const componentName = args.getComponentName();
    const scssCompletePath = args.getScssCompletePath();
    const foldersCompletePath = args.getComponentFolderCompletePath();
    const scssContent = getScssContent(componentName);
    if (!fs.existsSync(foldersCompletePath)) fs.mkdirSync(foldersCompletePath, { recursive: true });
    fs.writeFileSync(scssCompletePath, scssContent);
}

function getScssContent(componentName) {
    return `.${componentName} {
\t
}
`;
}

module.exports = createScss;