const fs = require("fs");
const args = require("../logic/args");

function createUtil() {
    const utilName = args.getUtilName();
    const tsCompletePath = args.getUtilTsCompletePath();
    const foldersCompletePath = args.getUtilFolderCompletePath();
    const tsContent = getTsContent(utilName);
    if (!fs.existsSync(foldersCompletePath)) fs.mkdirSync(foldersCompletePath, { recursive: true });
    fs.writeFileSync(tsCompletePath, tsContent);
}

function getTsContent(utilName) {
    const utilNameCap = utilName.upperFirstLetter();
    const utilNameUncap = utilName.lowerFirstLetter();
    
    return `class ${utilNameCap} {
\t
}

export const ${utilNameUncap} = new ${utilNameCap}();
`;
}

module.exports = createUtil;