const args = require("./args");
const displayHelp = require("./display-help");
const displayVersion = require("./display-version");
const createFc = require("../creators/create-fc");
const createFcScss = require("../creators/create-fc-scss");
const createFcModule = require("../creators/create-fc-module");
const createFcProps = require("../creators/create-fc-props");
const createFcScssProps = require("../creators/create-fc-scss-props");
const createFcModuleProps = require("../creators/create-fc-module-props");
const createCc = require("../creators/create-cc");
const createCcProps = require("../creators/create-cc-props");
const createCcState = require("../creators/create-cc-state");
const createCcPropsState = require("../creators/create-cc-props-state");
const createModel = require("../creators/create-model");
const createService = require("../creators/create-service");
const createUtil = require("../creators/create-util");

function doIt() {
    if(args.isFirstHelp()) return displayHelp();
    if(args.isFirstVersion()) return displayVersion();
    if(args.isFirstFC() && args.containScss() && args.containProps()) return createFcScssProps();
    if(args.isFirstFC() && args.containModule() && args.containProps()) return createFcModuleProps();
    if(args.isFirstFC() && args.containScss()) return createFcScss();
    if(args.isFirstFC() && args.containModule()) return createFcModule();
    if(args.isFirstFC() && args.containProps()) return createFcProps();
    if(args.isFirstFC()) return createFc();
    if(args.isFirstCC() && args.containPropsState()) return createCcPropsState();
    if(args.isFirstCC() && args.containProps()) return createCcProps();
    if(args.isFirstCC() && args.containState()) return createCcState();
    if(args.isFirstCC()) return createCc();
    if(args.isFirstModel()) return createModel();
    if(args.isFirstService()) return createService();
    if(args.isFirstUtil()) return createUtil();
}

module.exports = doIt;